import BP from '../../styles/blogpost.module.css';

const BlogPost = props => 
  <>
    <div className={BP.container} >
    {/* style={{ display: 'block', width: '50%', background: 'ivory', margin: '1% auto', boxShadow: '5px 5px 15px gray', borderRadius: '20px', flexDirection: 'column'}} */}
        <img src={props.image} alt='alt' className={BP.imgStyle}/>
        {/* style={{ height: 150, width: '100%', objectFit: 'fill'}} */}
      <div id='textDiv' className={BP.textBox}>
      {/* style={{ maxHeight: 150, width: '100%', padding: '2%', overflow: 'auto'}} */}
        <a href={props.link} className={BP.blackLink} target='_blank' rel='noopener noreferrer'><h2 className={BP.title}>{props.title}</h2></a>      
        {/* style={{ textAlign: 'left', marginLeft: -10 }} */}
        <p>Author: {props.author}</p>
        <p>{props.description}</p>
      </div>
    </div>
  </>

export default BlogPost;
