    query: ALL_USERS
  }).then(res => {
    return res;
  }).catch(err => console.log(err));
  const paths = usernamesQuery.data.allUsers.map(u => `/user/${encodeURIComponent(u.username)}`);
  return {
    paths,
    fallback: false
  };
}
