import React, {useState} from 'react'
// import '../styles/pages/test.css'
import { connect } from 'react-redux'
import Layout from '../components/Layout'
import Head from 'next/head'

const Test = () => {
    let fileInput = React.createRef()


    const  uploadToImgur = async (file) => {
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/x-www-form-urlencoded");
        myHeaders.append("Authorization", "Client-ID cc972a94b762b77");
        
        var formdata = new FormData();
        formdata.append("image", file, "/C:/Users/Sebastian/OneDrive/Pictures/aang.jpg");
        
        var requestOptions = {
          method: 'POST',
          headers: myHeaders,
          body: formdata,
          redirect: 'follow'
        };
        
        const response = await fetch("https://api.imgur.com/3/upload", requestOptions)
        
        console.log(response)

        setImageURL(URL.createObjectURL(file))
    }

    const [imageURL, setImageURL] = useState("")
    

    return (
        <Layout>
            <div className="navbar-height" />
            <h1 className="testt">test</h1>
            <input type="file" ref={fileInput} />
            <br />
            <button onClick={() => uploadToImgur(fileInput.current.files[0])}>set</button>
            <img src={imageURL} alt="hello" />
        </Layout>
    )
}

export default connect(
    null
)(Test)
