// import '../styles/pages/test.css'
import { connect } from 'react-redux'
import Layout from '../components/Layout'
import Head from 'next/head'

const Test = () => {
    let fileInput = React.createRef()
    const [showImg, setShowImg] = useState(false)
    const imgHTML = showImg ? <img src={URL.createObjectURL(fileInput.current.files[0])} /> : <p>img</p>
    // const handleSubmit = (e) => {
    //     event.preventDefault()
    // }
    // setShowImg(!showImg)
    return (
        <Layout>
            <div className="navbar-height" />
            <h1 className="testt">test</h1>
            {/* <form onSubmit={handleSubmit}> */}
            <input type="file" ref={fileInput} />
            <br />
            <button onClick={() => console.log(fileInput.current.files[0])}>submit</button>
            {/* </form> */}
            {imgHTML}
        </Layout>
    )
}

export default connect(
    null
)(Test)
